import React,{Component} from 'react';
import {Typography} from '@material-ui/core';
import {FuseAnimate} from '@fuse';
import {connect} from 'react-redux';
import {withRouter} from 'react-router-dom'
import {bindActionCreators} from 'redux';
import {Chart} from 'react-chartjs-2';
import Widget1 from './widgets/Widget1';
import Widget2 from './widgets/Widget2';
import Widget3 from './widgets/Widget3';
import Widget4 from './widgets/Widget4';
import Widget5 from './widgets/Widget5';
import Widget6 from './widgets/Widget6';
import Widget7 from './widgets/Widget7';
import Widget8 from './widgets/Widget8';
import Widget9 from './widgets/Widget9';
import Widget10 from './widgets/Widget10';
import withReducer from 'app/store/withReducer';
import * as Actions from './store/actions'
import reducer from './store/reducers';

class Dashboard extends Component{
    constructor(props)
    {
        super(props);

        Chart.pluginService.register({
            afterDatasetsDraw: function (chart, easing) {
                // Only activate the plugin if it's made available
                // in the options
                if (
                    !chart.options.plugins.xLabelsOnTop ||
                    (chart.options.plugins.xLabelsOnTop && chart.options.plugins.xLabelsOnTop.active === false)
                )
                {
                    return;
                }

                // To only draw at the end of animation, check for easing === 1
                const ctx = chart.ctx;

                chart.data.datasets.forEach(function (dataset, i) {
                    const meta = chart.getDatasetMeta(i);

                    if ( !meta.hidden )
                    {

                        meta.data.forEach(function (element, index) {

                            // Draw the text in black, with the specified font
                            ctx.fillStyle = 'rgba(255, 255, 255, 0.7)';
                            const fontSize = 13;
                            const fontStyle = 'normal';
                            const fontFamily = 'Roboto, Helvetica Neue, Arial';
                            ctx.font = Chart.helpers.fontString(fontSize, fontStyle, fontFamily);

                            // Just naively convert to string for now
                            const dataString = dataset.data[index].toString() + 'k';

                            // Make sure alignment settings are correct
                            ctx.textAlign = 'center';
                            ctx.textBaseline = 'middle';
                            const padding = 15;
                            const startY = 24;
                            const position = element.tooltipPosition();
                            ctx.fillText(dataString, position.x, startY);

                            ctx.save();

                            ctx.beginPath();
                            ctx.setLineDash([5, 3]);
                            ctx.moveTo(position.x, startY + padding);
                            ctx.lineTo(position.x, position.y - padding);
                            ctx.strokeStyle = 'rgba(255,255,255,0.54)';
                            ctx.stroke();

                            ctx.restore();
                        });
                    }
                });
            }
        });
    }


    componentDidMount()
    {
        this.props.getWidgets();
    }

    render()
    {
        const {widgets} = this.props;
        if (!widgets )
        {
            return 'Loading..';
        }
        return (

        <div className="flex flex-col md:flex-row sm:p-8 container">
                <div className="flex flex-1 flex-col min-w-0 ">
                               <FuseAnimate delay={600}>
                                    <Typography className="px-16 pb-8 text-18 font-300 ">
                                        UpComing Events
                                    </Typography>
                                </FuseAnimate>
                                <div className="widget w-full p-16 pb-32">
                                    <Widget10 data={widgets.widget9}/>
                                </div>
                </div>

                <div className="flex flex-wrap w-full md:w-320 pt-16">
                    <div className="mb-30 w-full sm:w-1/2 md:w-full">
                               <FuseAnimate delay={600}>
                                    <Typography className="px-16 pb-8 text-18 font-300 ">
                                        Event History
                                    </Typography>
                                </FuseAnimate>
                                {/* <div className="widget w-full p-16"> */}
                                    <Widget9 data={widgets.widget9}/>
                                {/* </div> */}
                    </div>
               </div> 



           </div>     

        )
    };


}


function mapDispatchToProps(dispatch)
{
    return bindActionCreators({
        getWidgets: Actions.getWidgets
    }, dispatch);
}

function mapStateToProps({Dashboard})
{
    return {
        widgets: Dashboard.widgets.data
    }
}

export default withReducer('Dashboard', reducer)(withRouter(connect(mapStateToProps, mapDispatchToProps)(Dashboard)));
